import sqlite3

def init_db():
    conn = sqlite3.connect('strategies.db')
    c = conn.cursor()
    c.execute('''CREATE TABLE IF NOT EXISTS best_strategy (
                    id INTEGER PRIMARY KEY,
                    strategy TEXT,
                    total_return REAL,
                    final_value REAL,
                    starting_value REAL,
                    num_transactions INTEGER,
                    current_holdings INTEGER,
                    annualized_return REAL,
                    initial_investment REAL,
                    ranking_metric TEXT,
                    skip_top_n INTEGER,
                    depth INTEGER,
                    start_date TEXT,
                    end_date TEXT
                )''')
    c.execute('''CREATE TABLE IF NOT EXISTS prior_champion (
                    id INTEGER PRIMARY KEY,
                    strategy TEXT,
                    total_return REAL,
                    final_value REAL,
                    starting_value REAL,
                    num_transactions INTEGER,
                    current_holdings INTEGER,
                    annualized_return REAL,
                    initial_investment REAL,
                    ranking_metric TEXT,
                    skip_top_n INTEGER,
                    depth INTEGER,
                    start_date TEXT,
                    end_date TEXT
                )''')
    conn.commit()
    conn.close()

init_db()


